// src/stream/state.ts

export class StreamState {
    private startTime: number | null = null; // When the stream starts
    private totalActiveTime: number = 0; // Total active time in milliseconds

    // Start the stream
    public start(): void {
      if (this.startTime === null) {
        this.startTime = Date.now();
      }
    }

    // Pause the stream
    public pause(): void {
      if (this.startTime !== null) {
        this.totalActiveTime += Date.now() - this.startTime; // Add elapsed time
        this.startTime = null; // Reset start time
      }
    }

    // Resume the stream
    public resume(): void {
      if (this.startTime === null) {
        this.startTime = Date.now(); // Set new start time
      }
    }

    // Get total active time in seconds
    public getTotalActiveTime(): number {
      if (this.startTime !== null) {
        return Math.floor((this.totalActiveTime + (Date.now() - this.startTime)) / 1000); // Convert ms to seconds
      }
      return Math.floor(this.totalActiveTime / 1000); // Convert ms to seconds
    }

    // Reset the stream state
    public reset(): void {
      this.startTime = null;
      this.totalActiveTime = 0;
    }
  }
